export function sanitizePromptMadLib(value,maxLen=420){const txt=String(value||"").replace(/\s+/g," ").trim();return txt.length>maxLen?`${txt.slice(0,maxLen-3)}...`:txt;}

export function buildPromptMadLibNotes(cfg,{includeArtifact=true}={}){
  const notes=[];
  if(cfg?.promptIntent) notes.push(`Run intent: ${sanitizePromptMadLib(cfg.promptIntent)}`);
  if(cfg?.promptLensEmphasis) notes.push(`Analytic emphasis: ${sanitizePromptMadLib(cfg.promptLensEmphasis)}`);
  if(cfg?.promptHardConstraints) notes.push(`Hard constraints: ${sanitizePromptMadLib(cfg.promptHardConstraints,560)}`);
  if(cfg?.promptOutputStyle) notes.push(`Output style: ${sanitizePromptMadLib(cfg.promptOutputStyle)}`);
  if(includeArtifact&&cfg?.promptArtifactFocus) notes.push(`Artifact focus: ${sanitizePromptMadLib(cfg.promptArtifactFocus)}`);
  return notes;
}

export function appendPromptMadLibBlock(base,cfg,{includeArtifact=true}={}){
  const notes=buildPromptMadLibNotes(cfg,{includeArtifact});
  if(!notes.length) return base;
  return `${String(base||"").trim()}\n\nPrompt controls:\n- ${notes.join("\n- ")}`;
}

export function buildProbeCompactFallbackPrompt(target,discName){return `Generate strict JSON only for this research probe.\n\nTopic: "${target}"\nDiscipline: "${discName}"\n\nReturn exactly:\n{\n  "summary": "2-3 concise sentences",\n  "terms": [\n    {"label":"term","centrality":0.7,"description":"one-sentence definition"}\n  ],\n  "claims_or_findings": ["claim"],\n  "citations": [],\n  "confidence_notes": "brief limits"\n}\n\nRules:\n- 6-8 terms\n- 3-4 claims_or_findings\n- citations must be []\n- no markdown, no prose before/after JSON`; }

export function buildProbeSystemPrompt(cfg={}){const base="You are a research expert. Respond only with valid JSON.";const notes=buildPromptMadLibNotes(cfg,{includeArtifact:false});if(!notes.length) return base;return `${base}\n${notes.map(line=>`- ${line}`).join("\n")}`;}

