export function buildSynthesisPrompt(target,probeResults,quality,cfg={}){const allTerms=probeResults.map(r=>`DISCIPLINE ${r.discId} (${DISCS[r.discId]?.name}):\nSummary: ${r.summary}\nTerms: ${(r.terms||[]).map(t=>t.label).join(", ")}\nClaims: ${(r.claims_or_findings||[]).join("; ")}`).join("\n\n");const styleLine=cfg?.promptOutputStyle?`\nStyle preference: ${sanitizePromptMadLib(cfg.promptOutputStyle)}.`:"";const emphasisLine=cfg?.promptLensEmphasis?`\nSynthesis emphasis: ${sanitizePromptMadLib(cfg.promptLensEmphasis)}.`:"";const hardConstraintLine=cfg?.promptHardConstraints?`\nHard constraints: ${sanitizePromptMadLib(cfg.promptHardConstraints,560)}.`:"";return `These are research summaries about "${target}" from ${DISCS.length} independent disciplines:\n\n${allTerms}\n\nIdentify:\n1. CONVERGENT: concepts that appear across 2+ disciplines\n2. CONTRADICTORY: genuine tensions between disciplines\n3. EMERGENT: insights visible only after cross-disciplinary integration${styleLine}${emphasisLine}${hardConstraintLine}\n\nReturn only this JSON:\n{\n  "convergent": [\n    {"label": "shared concept", "disciplines": [0,1,3], "description": "why it converges"}\n  ],\n  "contradictory": [\n    {"label": "tension description", "disciplines": [0,2], "description": "what the tension is"}\n  ],\n  "emergent": [\n    {"label": "synthesis insight", "description": "what appears only in integration"}\n  ]\n}\n\nInclude ${quality.synthConvergent} convergent, ${quality.synthContradictory} contradictory, and ${quality.synthEmergent} emergent items.`;}

export function buildRedTeamPrompt(target,probeResults,synthResult,cfg={}){const probeSummaries=probeResults.map(r=>`${DISCS[r.discId]?.name}: ${r.summary} | Claims: ${(r.claims_or_findings||[]).join("; ")}`).join("\n");const contradictions=(synthResult.contradictory||[]).map(c=>`${c.label} (${(c.disciplines||[]).map(i=>DISCS[i]?.name||i).join(" vs ")})`).join("\n");const emergent=(synthResult.emergent||[]).map(e=>e.label).join(", ");const focusLine=cfg?.promptArtifactFocus?`\nCritique focus: ${sanitizePromptMadLib(cfg.promptArtifactFocus)}.`:"";const hardConstraintLine=cfg?.promptHardConstraints?`\nRespect hard constraints: ${sanitizePromptMadLib(cfg.promptHardConstraints,560)}.`:"";return `You are a skeptical reviewer. Attack weak joints, missing confounders, overgeneralizations, and citation risks.${focusLine}${hardConstraintLine}\n\nTopic: ${target}\n\nProbe summaries:\n${probeSummaries}\n\nContradictions:\n${contradictions}\n\nEmergent ideas:\n${emergent}\n\nReturn a concise critique with:\n- 3-6 high-risk claims\n- what evidence would falsify them\n- missing perspectives or datasets\n- any citation red flags`;}
