{  "convergent": [
    {
      "label": "Regime Change Dynamics",
      "disciplines": [0,1,3],
      "description": "Multiple disciplines acknowledge regime change as either an explicit Western objective (Ctr-Right) or unintended consequence of military intervention (Left/Ctr-Left), though motivations differ between punitive containment vs. democratic aspirations."
    },
    {
      "label": "Economic Collapse & Military Prioritization",
      "disciplines": [0,3,4],
      "description": "Left and Ctr-Right concur that Iran's economic freefall stems from prioritizing military/proxy expenditures over public welfare, though Left blames imperialism while Ctr-Right emphasizes regime mismanagement."
    },
    {
      "label": "Civilian Impact of Strikes",
      "disciplines": [1,2,3],
      "description": "Center-Left, Center, and Ctr-Right all document civilian casualties from military operations but frame them differently: as humanitarian failures (Ctr-Left), collateral damage (Center), or regime propaganda tools (Ctr-Right)."
    }
  ],
  "contradictory": [
    {
      "label": "Intervention Efficacy",
      "disciplines": [0,2],
      "description": "Left rejects military action as imperialist escalation harming civilians, while Center frames strikes as necessary security measures enabling diplomatic leverage against nuclear threats."
    },
    {
      "label": "Regime Resilience Assessment",
      "disciplines": [3,4],
      "description": "Ctr-Right emphasizes regime fragility from war/economic strain, whereas Right asserts Iran's rapid military reconstitution proves enduring threat capacity despite setbacks."
    }
  ],
  "emergent": [
    {
      "label": "Coercion Paradox",
      "description": "Cross-analysis reveals military pressure and sanctions intended to weaken the regime instead consolidate elite cohesion by justifying repression and diverting resources to security apparatuses, per Left economic critiques and Ctr-Right survivalism."
    },
    {
      "label": "Proxy War Feedback Loop",
      "description": "Integration shows how Iran's regional proxies (emphasized by Right/Ctr-Right) both extend its influence and deepen economic isolation (noted by Left/Ctr-Left), creating self-reinforcing cycles of external confrontation and internal deprivation."
    },
    {
      "label": "Narrative Weaponization",
      "description": "Synthesis exposes how all factions instrumentalize civilian suffering - Left as imperialist brutality, Center as regrettable necessity, Right as regime manipulation - revealing shared rhetorical reliance on victimhood despite divergent policy prescriptions."
    }
  ]
}
