{  "convergent": [
    {
      "label": "US-Israel Military Coordination",
      "disciplines": [0,1,2,4],
      "description": "All four disciplines highlight Israel’s military collaboration with the U.S., whether through joint strikes (0,1,2,4) or strategic alignment (4), though motivations differ (security vs. regime change vs. apartheid critique)."
    },
    {
      "label": "Civilian Harm",
      "disciplines": [1,2],
      "description": "Center-Left and Center disciplines both emphasize civilian casualties (school bombing, regional evacuations) as direct consequences of Israeli strikes."
    },
    {
      "label": "Regime Change Agenda",
      "disciplines": [1,4],
      "description": "Center-Left and Right disciplines reference Israel’s explicit targeting of Iranian leadership (Khamenei, Pezeshkian) to destabilize or replace the regime, framed as existential defense (Right) or escalatory risk (Center-Left)."
    }
  ],
  "contradictory": [
    {
      "label": "Legitimacy of Strikes",
      "disciplines": [0,2],
      "description": "Left critiques strikes as enabling apartheid and Palestinian displacement (via Nakba-as-structure), while Center frames them as defensive preemption against nuclear threats."
    },
    {
      "label": "Strategic Concessions vs. Sovereignty Defense",
      "disciplines": [3,4],
      "description": "Center-Right portrays Israeli territorial withdrawals in Gaza as weakness under pressure, whereas Right emphasizes uncompromising sovereignty defense against Iran."
    }
  ],
  "emergent": [
    {
      "label": "Multi-Theater Conflict Strategy",
      "description": "Israel simultaneously engages Iran militarily (disciplines 0-2,4) and negotiates fragile ceasefires with Hamas (3), suggesting a dual approach to regional threats that risks overextension."
    },
    {
      "label": "Narrative Weaponization",
      "description": "Pro-Israel factions leverage Iranian regime atrocities (Left’s critique) to justify strikes (Right/Center), while anti-Israel factions use civilian harm (Center-Left/Center) and Palestinian displacement (Left) to delegitimize actions."
    },
    {
      "label": "Diplomatic Vacuum",
      "description": "Cross-disciplinary evidence reveals failed US-Iran nuclear talks (1,2) and rejected US-backed Palestinian resettlement (3), exposing a systemic collapse of diplomacy favoring unilateral military solutions."
    }
  ]
}
